import React, {useContext, useEffect} from 'react';
import {useParams} from "react-router-dom";
import {Context} from "../../index";
import {Space} from "antd";
import Title from "antd/es/typography/Title";
import {fetchOneEvent} from "../../http/eventAPI";
import {fetchOneHall} from "../../http/hallAPI";
import moment from "moment";
import {observer} from "mobx-react-lite";
import HallGenerate from "../../components/event/hall/hallGenerate";

const Hall = () => {
    const {id} = useParams();
    const {hall} = useContext(Context);
    useEffect(() => {
        fetchOneHall(id).then(data => hall.setHall(data));
        console.log(hall.hall)
    }, []);
    if (!hall ||  !hall.hall) {
        if (!hall.hall.event ) {
            return null; // Возвращаем null, если нет данных о событии 
        }
    }
        return (
        <Space size={0} direction={"vertical"} style={{margin: 10}}>
            <Title>{hall.hall.event? hall.hall.event.title : null}</Title>
            <Space>
                <Title level={4}>{hall.hall.event? moment(hall.hall.event.dateTime).locale('ru').format('DD MMMM'): null} •</Title>
                <Title level={4}>{hall.hall.event? moment(hall.hall.event.dateTime).locale('ru').format('ddd '): null} •</Title>
                <Title level={4}>{hall.hall.event? moment(hall.hall.event.dateTime).locale('ru').format('HH:mm'): null} •</Title>
                <Title level={4}>{hall.hall.event? hall.hall.event.hall.name :null } </Title>
                <HallGenerate numberRows={hall.event.hall.numberRows} numberSeatsInRow={hall.event.hall.numberSeatsInRow} hallOptionPrice={hall.event.hollOptionPrice} />
            </Space>
        </Space>
    );
};

export default observer(Hall);
