import React from 'react';
import {observer} from "mobx-react-lite";
import {useNavigate} from "react-router-dom";
import {Card} from "antd";
import {EVENT_ROUTE, ORDER_ROUTE} from "../../utils/consts";
import moment from "moment/moment";
import Title from "antd/es/typography/Title";
import AddressLink from "../event/AddressLink";

const OrderCard = ({thisOrder}) => {
            const navigate = useNavigate()
            return (
            <Card
                title= {moment(thisOrder.dateTime).locale('ru').format('DD MMMM HH:mm ddd')}
                hoverable
                style={{width: 240}}
                onClick={() => navigate(ORDER_ROUTE + '/' + thisOrder.id)}
                cover={
                    <div style={{position: 'relative', paddingBottom: '150%'}}>
                        <img
                            alt="photo"
                            src={process.env.REACT_APP_API_URL + thisOrder.img}
                            style={{
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                width: '100%',
                                height: '100%',
                                objectFit: 'cover',
                            }}
                        />

                    </div>
                }
            >
                <Title level={4} style={{height: '3em'}}>
                    {thisOrder.title}
                </Title>
                <Title level={5} type="secondary" style={{height: '1em'}}>
                    {thisOrder.dateTime
                        ? moment(thisOrder.dateTime).locale('ru').format('DD MMMM HH:mm ddd')
                        : ''}
                </Title>
                <Title level={5} type="secondary" style={{height: '1em'}}>
                    {
                        thisOrder.hall ? thisOrder.hall.name : thisOrder.entrance ? thisOrder.entrance.name : null
                    }

                </Title>
                <AddressLink
                style={{marginTop: -10}}
                name={thisOrder ? thisOrder.addressName : null}
                adress={thisOrder ? thisOrder.address : null}
                styleIcon={{width:'3em', height: '3em', color:'#999898', }}
                styleTitle={{height: '1em', color:'#999898', marginTop: 5,}}
                level={5}
                />

            </Card>
            )

};

export default observer(OrderCard);
