import React from 'react';
import {Button, DatePicker, Form, Input} from "antd";
import onCreate from "../../services/userService/authService";

const Orders = () => {
    return (
        <div>
            <Form.Item
                label="Почта"
                name="email"
                rules={[
                    {
                        message: 'почта не корректна',
                        required: true,
                        type: 'email',
                    },
                ]}
            >
                <Input/>
            </Form.Item>

            <Form.Item

                label="Имя"
                name="name"
                rules={[
                    {

                        message: 'не корректно',
                        required: false,
                    },
                ]}
            >
                <Input/>
            </Form.Item>
            <Form.Item

                label="Фамилия"
                name="surname"
                rules={[
                    {
                        message: 'введите фамилию',
                        required: false,
                    },
                ]}
            >
                <Input/>
            </Form.Item>
            <Form.Item

                label="Дата рождения"
                name="birthday"
                rules={[
                    {

                        message: 'введите дату',
                        type: 'date',
                        required: false,
                    },
                ]}
            >
                <DatePicker sty placeholder=""/>
            </Form.Item>
            <Form.Item style={{textAlign: 'center'}}>
                <Button type="primary" htmlType="submit" style={{width: 200, height: 40, fontSize: 18,  backgroundColor:'#722ed1'}}
                        onClick={() => {
                            setMessage('')
                            form
                                .validateFields()
                                .then((values) => {
                                    onCreate(values, user, isRegistration)
                                        .then(result => {
                                            if (result === true) {
                                                console.log(result)
                                                setIsRegistration(false);
                                                form.resetFields();
                                                onCancel();
                                                setMessage('')
                                            } else {
                                                isRegistration?
                                                    setMessage("Данный email уже зарегестрирован")
                                                    :
                                                    setMessage("Логин и пароль не совпадают")
                                            }
                                        });

                                })
                                .catch((info) => {
                                    console.log('Failed:', info);
                                });
                        }}
                >
                    {isRegistration ?
                        'Зарегестрироваться'
                        :
                        'Войти'
                    }
                </Button>
            </Form.Item>
        </div>
    );
};

export default Orders;
