import React from 'react';
import {Page, Text, View, Document, StyleSheet, Font, Image, Line, Svg} from '@react-pdf/renderer';
import Hall from "../../pages/event/hall";
import LogoTicket from "../../assets/LogoTicket.png";

Font.register({
    family: "Robot",
    src:
        "https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto/roboto-light-webfont.ttf"
});
Font.register({
    family: "RobotBold",
    src:
        "https://cdnjs.cloudflare.com/ajax/libs/ink/3.1.10/fonts/Roboto/roboto-bold-webfont.ttf"
});
// Create styles

const styles = StyleSheet.create({
    body: {
        paddingTop: 35,
        paddingBottom: 65,
        paddingHorizontal: 35,
        fontFamily: "Robot",
    },
    page: {
        flexDirection: "row",
        backgroundColor: "#E4E4E4",
        fontFamily: "Robot",
    },
    section: {
        margin: 10,
        padding: 10,
        flex: 1,
        flexDirection: "column",
        justifyContent: "space-between",
    },
    text: {
        fontFamily: "Robot",
    },
    line: {
        marginBottom: 20,
        borderBottom: "1pt solid #391085",
    },
    logo: {
        width: 240,
        height: 62.5,
        marginTop: 40,
        marginLeft:-95,
        marginBottom: 20,
        alignSelf: "center",
    },
    textBold: {
        fontFamily: "RobotBold",
    }
});
const handleDownload = () => {
    const blobProvider = (
        <BlobProvider document={<Ticket response={response} />} fileName="ticket.pdf">
            {({ blob, url, loading, error }) => {
                if (loading) {
                    return <Text>Loading...</Text>;
                }
                if (error) {
                    return <Text>Error occurred while generating PDF.</Text>;
                }
                return (
                    <a href={url} download>
                        Download Ticket
                    </a>
                );
            }}
        </BlobProvider>
    );
    window.open(blobProvider.props.document.props.fileName);
};
const Ticket = () => {
    const src = ' https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=' + process.env.REACT_APP_CLIENT_URL  + 'event/1';
    console.log(' https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=' + process.env.REACT_APP_CLIENT_URL  + 'event/1')
    return (
        <Document>
            <Page orientation="landscape" size="A4" style={styles.page} wrap={false}>
                <View style={styles.section}>
                    <Text style={{fontSize: 28}}>Заголовок</Text>
                    <Line style={styles.line}/>
                    <Text style={{fontSize: 14, color:'gray', flex: 1}}>
                        Дата и время:
                        <Text style={{ color: 'black'}}>
                            Rockstar Bar. ул. Пушкина, 29А
                        </Text>
                    </Text>
                    <Text style={{fontSize: 14, color:'gray', flex: 1}}>
                        Стоимость:
                        <Text style={{ color: 'black'}}>
                            1 012 руб.
                        </Text>
                    </Text>

                    <Text style={{fontSize: 14, color:'gray', flex: 1}}>
                        Категория:
                        <Text style={{ color: 'black'}}>
                            Танцпол
                        </Text>
                    </Text>
                    <Text style={{fontSize: 12, flex: 1, textAlign: 'center'}}>
                        {"Электронный билет \n Номер: 1231180948 / 44220189 \n Дата покупки: 20.09.2023 09:39 \n Выпущен на Merop"}
                    </Text>

                </View>
                <View style={styles.section}>
                    <View style={styles.section}>


                        <Text style={styles.textBold}>Как работает билет</Text>
                        <Text style={{fontSize: 12, flex: 1}}>По билету можно пройти только один раз, его нельзя
                            копировать и перепродавать. Не выкладывайте билет в соцсети: другой человек может
                            использовать штрихкод и пройти вместо вас
                        </Text>
                        <Text style={styles.textBold}>Как вернуть</Text>
                        <Text style={{
                            fontSize: 12,
                            flex: 1
                        }}>{"Если событие отменят, заменят или перенесут или у вас просто не получается на него попасть пишите на почту \n MeropsRoot@hotmail.com"}
                        </Text>
                        <Text style={styles.textBold}>Участники мероприятия обязаны</Text>
                        <Text style={{fontSize: 12}}>- соблюдать правила поведения в общественных местах;</Text>
                        <Text style={{fontSize: 12}}>- занимать места согласно купленному билету;</Text>
                        <Text style={{fontSize: 12}}>- сохранять билет до конца мероприятия и предъявлять по требованию
                            администрации и сотрудников службы охраны.</Text>

                        <Image
                            cache={false}
                            src={LogoTicket}
                            style={styles.logo}
                        />

                        <Image
                            cache={false}
                            src={src}
                            style={{width:62.5, height:62.5, float: "right", marginLeft:260, marginTop:-82}}
                        />

                    </View>
                </View>
            </Page>
            <button onClick={handleDownload}>Download Ticket</button>
        </Document>
    )
};

export default Ticket;
