import React, {useContext, useEffect, useState} from 'react';
import {fetchOneEvent} from "../../http/eventAPI";
import {useParams} from "react-router-dom";
import {Context} from "../../index";
import {Col, Collapse, Image, Row, Typography} from 'antd';
import Logo from "../../assets/logo.png";
import {observer} from "mobx-react-lite";
import Title from "antd/es/typography/Title";
import ShareButton from "../../components/event/ShareButton";
import AddressLink from "../../components/event/AddressLink";

const { Text, Link } = Typography;
const { Panel } = Collapse;

const Event = () => {
    const {id} = useParams()
    const {event} = useContext(Context)
    const [expanded, setExpanded] = useState(false);
    useEffect(() => {
        fetchOneEvent(id).then(data => event.setEvent(data))

    }, []);
    if (!event || !event.event ) {
        return null; // Возвращаем `null`, если нет данных о событии или свойства `event.event`
    }
    return (
        <Row style={{margin:'5%',  }}>
            <Col md={6}>
            <Image
                alt="photo"
                src={process.env.REACT_APP_API_URL + event.event.img}
                width={'20vw'}
               height={'30vw'}
                style={{
                    borderRadius: '5%',
                    padding: '5px',
                    objectFit: 'cover',
                }}
            />
            </Col>
            <Col md={18} >
                <Title>
            {event.event.title}
                </Title>
                <Title level={3}> {
                    event.event.hall? event.event.hall.name : event.event.entrance ? event.event.entrance.title : null
                }
                </Title>
                <AddressLink  adress={event.event.hall? event.event.hall.adress : event.event.entrance ? event.event.entrance.adress : null} />
                <div>
                    {expanded ? text : shortenedText}
                    <button onClick={handleClick}>{expanded ? 'Свернуть' : 'Подробнее'}</button>
                </div>
                <ShareButton />
            </Col>
        </Row>
    );
};

export default observer(Event);
