import React from 'react';
import {Card, Typography} from "antd";
import Meta from "antd/es/card/Meta";
import {observer} from "mobx-react-lite";
import moment from 'moment';
import {EVENT_ROUTE} from "../../utils/consts";
import {useNavigate} from "react-router-dom";
import 'moment/locale/ru';
const {Text, Title} = Typography

const EventItem = ({thisEvent}) => {
    const  navigate = useNavigate()
    return (
        <Card
            hoverable
            style={{
                width: 240,
            }}
            onClick={() => navigate(EVENT_ROUTE + '/' + thisEvent.id)}
            cover={<img alt="example" src={process.env.REACT_APP_API_URL + thisEvent.img} />}
        >
            <div style={{
                position: 'absolute',
                top: '10px',
                left: '10px',
                backgroundColor: '#722ed1',
                color: 'white',
                borderRadius: '50%',
                padding: '5px'
            }}>12+</div>
            <Title level={4}>{thisEvent.title}</Title>
            <Title level={5}>{moment(thisEvent.dateTime).locale('ru').format('DD MMMM HH:mm ddd')}</Title>
        </Card>
    );

};

export default observer(EventItem);
