import React from 'react';
import {Button, Card, Flex, Form, Input, Space, Typography} from "antd";
import {CloseOutlined} from "@ant-design/icons";
import Title from "antd/es/typography/Title";

const CreateEntrance = () => {
    const [form] = Form.useForm();
    return (
        <Space direction={"vertical"}>
            <Title level={4}>Типы входных билетов</Title>
        <Form
            layout="vertical"
            form={form}
            name="dynamic_form_complex"
            style={{
                maxWidth: 600,
            }}
            autoComplete="off"
            initialValues={{
                items: [{}],
            }}
            title="Входные билеты"
        >
            <Form.Item label="Адрес площадки" >
                <Input />
            </Form.Item>
            <Form.Item label="Название площадки" >
                <Input />
            </Form.Item>

            <Form.List name="option">
                {(fields, { add, remove }) => (
                    <div
                        style={{
                            display: 'flex',
                            rowGap: 16,
                            flexDirection: 'column',
                        }}
                    >
                        {fields.map((field) => (
                            <Card
                                size="small"
                                title={`Категория ${field.name + 1}`}
                                key={field.key}
                                extra={
                                    <CloseOutlined
                                        onClick={() => {
                                            remove(field.name);
                                            remove(field.totalSeat)
                                        }}
                                    />
                                }
                            >
                                <Form.Item label="Название категории" name={[field.name, 'name']}>
                                    <Input />
                                </Form.Item>

                                <Form.Item label="Мест в категорие" name={[field.totalSeat, 'name']}>
                                    <Input  />
                                </Form.Item>
                            </Card>
                        ))}

                        <Button type="dashed" onClick={() => add()} block>
                            Добавить категорию +
                        </Button>
                    </div>
                )}
            </Form.List>

            <Form.Item noStyle shouldUpdate>
                {() => (
                    <Typography>
                        <pre>{JSON.stringify(form.getFieldsValue(), null, 2)}</pre>
                    </Typography>
                )}
            </Form.Item>
            <Form.Item label="Всего мест"  >
                <Input disabled/>
            </Form.Item>
        </Form>
        </Space>
    );
};

export default CreateEntrance;
