import React, {useState} from 'react';
import {Button, Divider, notification, Row, Space} from "antd";
import Title from "antd/es/typography/Title";
import {MinusOutlined, PlusOutlined} from "@ant-design/icons";

const EntranceLine = ({thisEntranceOption}) => {
    const [counter, setCounter] = useState(0);
    const counterMinus = () => {
        if(counter >0){
            setCounter(counter -1)
        }
    };
    const [api, contextHolder] = notification.useNotification();
    const openNotification = () => {
        api.info({
            message: 'Больше билетов забронировать не получиться(',
            description:
                'This is the c'
        });
    };
    const counterPlus = () => {
        if(counter < thisEntranceOption.totalSeats && counter < 10){
            setCounter(counter +1)
        }
        else{
            openNotification();
        }

    };

    return (
        <Row style={{ display: 'flex', alignItems: 'center', justifyContent:'space-between', marginLeft:'2.5%', marginRight:'20%'}}>
            <Title level={3}>{thisEntranceOption.name}</Title>
          <Space style={{display: 'flex', alignItems: 'center'}}>
            <Button type="primary"
                    shape="circle"
                    icon={<MinusOutlined />}
                    style={{backgroundColor: '#722ed1'}}
                    onClick={counterMinus}
            />
              <Title level={2} style={{margin:3}}>{counter}</Title>
            <Button
                type="primary"
                shape="circle"
                icon={<PlusOutlined />}
                style={{backgroundColor: '#722ed1', }}
                onClick={counterPlus}
            />
          </Space>
            <Divider />
        </Row>
    );
};

export default EntranceLine;
