import React, {useRef, useState} from 'react';
import {Button, Carousel, ConfigProvider, Input, Space, Tour} from "antd";
import Title from "antd/es/typography/Title";
import Carouselcontroller from "../../components/creator/Controler/Carouselcontroller";

const ControllerCreator = () => {
    const [open, setOpen] = useState(false);
    const [ticketNumber, setTicketNumber] = useState('');
    const [status, setStatue] = useState('');
    const PostTicket = () =>{
        console.log('Введенный номер билета:', ticketNumber);
        if (ticketNumber.length !== 7){
            setStatue('error')

            return
        }
    }

    return (
        <Space direction={"vertical"} size={"small"}>
            <Space>
                <Title level={2}>
                   Контроль билетов
                </Title>
                {/*<Button style={{marginBottom:10}} type="text" onClick={() => setOpen(true)}>*/}
                {/*    <InfoCircleOutlined /> Как это работает?*/}
                {/*</Button>*/}
            </Space>


            <Space>
                <Input
                    status="status"
                    size="large"
                    placeholder="Номер билета"
                    maxLength={7}
                    type="number"
                    onChange={(e) => setTicketNumber(e.target.value)}
                />
                <Button type="primary" size="large" onClick={PostTicket}>
                    Ввод
                </Button>

            </Space>
            <Carouselcontroller />


        </Space>
    );
};

export default ControllerCreator;
