import React, {useContext, useState} from 'react';
import {Button, Card, Flex, Image} from "antd";
import Title from "antd/es/typography/Title";
import {Context} from "../../index";
import {observer} from "mobx-react-lite";
const CheckCardController = ({style}) => {
    const  { ticket} = useContext(Context)
    const bgColor = ticket.ticket.status ? '#52c41a' : '#ff4d4f';
    console.log(ticket)
    if(!!ticket.ticket){
        return (<div/>)
    }
    return (
        <Card
            hoverable
            style={{ ...style, width: '100%', height: 'auto', backgroundColor: bgColor }}
            bodyStyle={{
                padding: 0,
                overflow: 'hidden',
            }}
        >
            <Flex direction="column">
                <img
                    alt="avatar"
                    src={process.env.REACT_APP_API_URL + ticket.ticket.event.img}
                    style={{
                        width: '100%',  // Set width to 100%
                        height: 'auto',
                        objectFit: 'cover',
                    }}
                />
                <Flex
                    vertical
                    align="flex-end"
                    justify="space-between"
                    style={{
                        padding: 32,
                    }}
                >
                    <Title level={3}>
                        “antd - это язык дизайна интерфейса и библиотека пользовательского интерфейса для React.”
                    </Title>
                </Flex>
            </Flex>
        </Card>
    );
};

export default observer(CheckCardController);
