import React, { useContext } from 'react';
import { Card, Flex } from 'antd';
import { observer } from 'mobx-react-lite';
import { Context } from '../../index';
import Title from "antd/es/typography/Title";
const CheckCardController = ({ style }) => {
    const { ticket } = useContext(Context);

    if (!ticket.controllerTicket) {
        return null;
    }

    const bgColor = ticket.controllerTicket.status ? '#52c41a' : '#ff4d4f';

    return (
        <Card
            hoverable
            style={{ ...style, width: '100%', height: 'auto', backgroundColor: bgColor, padding:10 }}
            bodyStyle={{
                padding: 0,
                overflow: 'hidden',
            }}
        >
            <Flex direction="column">
                <img
                    alt="avatar"
                    src={process.env.REACT_APP_API_URL + ticket.controllerTicket.event.img}
                    style={{

                        width: 'auto',
                        height: '50vh',
                        objectFit: 'cover',
                    }}
                />
                <Flex
                    vertical
                    align="flex-end"
                    justify="space-between"
                    style={{
                        padding: 32,
                    }}
                >
                    <Title level={3}>
                        “antd - это язык дизайна интерфейса и библиотека пользовательского интерфейса для React.”
                    </Title>
                </Flex>
            </Flex>
        </Card>
    );
};

export default observer(CheckCardController);
