    import React, {useContext} from 'react';
    import {
    BarcodeOutlined,
    CalendarOutlined,
    EnvironmentOutlined, LogoutOutlined, PoweroffOutlined,
    TeamOutlined, UserOutlined
} from '@ant-design/icons';
    import { Menu } from 'antd';
    import {Context} from "../../index";
    import {observer} from "mobx-react-lite";
    function getItem(label, key, icon,  type) {
        return {
            key,
            icon,
            label,
            type,
        };
    }
    const items = [
        getItem('Мероприятия', 'events',<CalendarOutlined />, ),
        getItem('Залы', 'zal', <EnvironmentOutlined />, ),
        getItem('Покупки', 'buyers', <TeamOutlined />, ),
        getItem('Контролеры', 'controler', <BarcodeOutlined />, ),
        getItem('Обо мне', 'aboutMe', <UserOutlined />, ),
        getItem('Выйти', 'exit', <LogoutOutlined />, ),

    ];
    const CreatorMenu = () => {
        const  {creator} = useContext(Context)
        const onClick = (e) => {
            window.location.hash = (e.key)
        };
        return (
            <Menu
                onClick={onClick}
                style={{
                    width: 256,
                }}
                defaultSelectedKeys={['0']}
                defaultOpenKeys={['sub1']}
                mode="inline"
                items={items}
            />
        );
    };
    export default observer(CreatorMenu);
