import React, {useContext, useEffect, useState} from 'react';
import {fetchOneEvent} from "../../http/eventAPI";
import {useParams} from "react-router-dom";
import {Context} from "../../index";
import {Button, Col, Collapse, Image, Row, Space, Tag, Typography} from 'antd';
import Logo from "../../assets/logo.png";
import {observer} from "mobx-react-lite";
import Title from "antd/es/typography/Title";
import ShareButton from "../../components/event/ShareButton";
import AddressLink from "../../components/event/AddressLink";
import Paragraph from "antd/es/typography/Paragraph";

const { Text, Link } = Typography;
const { Panel } = Collapse;

const Event = () => {
    const {id} = useParams()
    const {event} = useContext(Context)
    const [expanded, setExpanded] = useState(false);
    const [rows, setRows] = useState(3);

    useEffect(() => {
        fetchOneEvent(id).then(data => event.setEvent(data))

    }, []);
    if (!event || !event.event ) {
        return null; // Возвращаем `null`, если нет данных о событии или свойства `event.event`
    }
    return (
        <Row style={{margin:'5%',  }}>
            <Col md={6}>
            <Image
                alt="photo"
                src={process.env.REACT_APP_API_URL + event.event.img}
                width={'20vw'}
               height={'30vw'}
                style={{
                    borderRadius: '5%',
                    padding: '5px',
                    objectFit: 'cover',
                }}
            />
            </Col>
            <Col md={18} >
                <Space direction="vertical">
                <Row>
                    <Tag style={{fontWeight:'bold'}} bordered={false}>{event.event.ageRating?  event.event.ageRating.age : null}+</Tag>
                    <Tag style={{fontWeight:'bold'}} bordered={false}>{event.event.type? event.event.type.name: null}</Tag>
                </Row>
                <Title>
            {event.event.title}
                </Title>

                <AddressLink name={ event.event.hall? event.event.hall.name : event.event.entrance ? event.event.entrance.title : null}
                             adress={event.event.hall? event.event.hall.adress : event.event.entrance ? event.event.entrance.adress : null}
                />

                    <Paragraph
                        ellipsis={{
                            rows,
                            symbol: 'Подробнее',
                            expandable: true,
                            onEllipsis: (ellipsis) => {
                                console.log('Ellipsis changed:', ellipsis);
                            },
                        }}
                        title={event.event.description}
                    >
                        {event.event.description}
                    </Paragraph>
                    <Button type={"primary"} style={{backgroundColor:'#391085', width:'10%', height:'5%'}}>{event.event.hall? "Выбрать место" : "Хочу билет"}</Button>
                {/*<ShareButton />*/}
                </Space>
            </Col>
        </Row>
    );
};

export default observer(Event);
