import React, {useRef, useState} from 'react';
import {Button, Carousel, ConfigProvider, Input, Space, Tour} from "antd";
import {EllipsisOutlined, InfoCircleOutlined} from "@ant-design/icons";
import Ticket from '../../assets/numberTicket.png'
import QrImg from '../../assets/QrScan.jfif'
import ruRU from "antd/es/locale/ru_RU";
import Title from "antd/es/typography/Title";
import Carouselcontroller from "../../components/creator/Controler/Carouselcontroller";

const contentStyle = {
    margin: 0,
    height: '160px',
    color: '#fff',
    lineHeight: '160px',
    textAlign: 'center',
    background: '#364d79',
}

const ControllerCreator = () => {
    const ref = useRef(null);

    const [open, setOpen] = useState(false);

    const steps = [
        {
            title: 'Варианты контроля',
            description: 'Есть 2 варианта контроля билета: по номеру и с помощью QR-кода',
        },
        {
            title: 'По номеру билета',
            cover: (
                <img
                    src={Ticket}
                />
            ),
            description: 'Введите в поле номер указаный в билете и нажмите "Ввод"',
            target: () => ref.current,
        },
        {
            title: 'По QR-коду',
            description: 'Необходимо предварительно авторезироваться под вашим аккаунтом на сайте, а после остакнировать билет, любым доступным сканером на телефоне, qr-код переведет вас на страницу билета.',
            cover: (
                <img
                    src={QrImg}
                />
            ),
        },
    ];
    return (
        <Space direction={"vertical"} size={"small"}>
            <Space>
                <Title level={2}>
                   Контроль билетов
                </Title>
                <Button style={{marginBottom:10}} type="text" onClick={() => setOpen(true)}>
                    <InfoCircleOutlined /> Как это работает?
                </Button>
            </Space>


            <Space ref={ref}>
                <Input size="large" placeholder="Номер билета"  maxLength='7' type="number" />
                <Button type="primary" size="large">
                    Ввод
                </Button>

            </Space>
            {/*<Carouselcontroller />*/}

                    <Carousel style={{margin:'10%', width:'60vw', height:'10vh', ...contentStyle}} autoplay >
                    {steps.map((step, index) => (
                        <div key={index} style={contentStyle}>
                            <h3>{step.title}</h3>
                            <p>{step.description}</p>
                            {step.content}
                        </div>
                    ))}
                </Carousel>
        </Space>
    );
};

export default ControllerCreator;
