import React, {useContext, useState} from 'react';
import {Alert, Button, DatePicker, Form, Input, Typography} from "antd";
import onCreate from "../../services/userService/authService";
import {Context} from "../../index";
import {observer} from "mobx-react-lite";
import {ReCAPTCHA} from "react-google-recaptcha";
const {Text, Link} = Typography

const RegLogForm = ({title, onCancel, setPassUpdate}) => {

    const [form] = Form.useForm();
    const [isRegistration, setIsRegistration] = useState(false);
    const [message, setMessage] = useState(false);
    const [buttonActiv, setButtonActiv] = useState(false);
    const {user} = useContext(Context)
    const [openPas, setOpenPas] = useState(false);
    if(isRegistration){
        title("Регистрация")
    }
    else{
        title("Авторизация")
    }
    const recap = () => {
        setButtonActiv(true)
    }
    return (
        <Form
            form={form}
            layout="vertical"
            name="form_in_modal"
            initialValues={{
                modifier: 'public',
            }}
        >
            {message && (
                <Alert type="error" message={message} showIcon />
            )}
            <Form.Item
                label="Почта"
                name="email"
                rules={[
                    {
                        message: 'почта не корректна',
                        required: true,
                        type: 'email',
                    },
                ]}
            >
                <Input/>
            </Form.Item>

            <Form.Item

                label="Пароль"
                name="password"
                rules={[
                    {
                        required: true,
                        message: 'введите пароль',
                    },
                ]}
            >
                <Input.Password/>
            </Form.Item>
            <div hidden={isRegistration}
                 style={{textAlign: 'right', cursor: 'pointer'}}
            >
                <Text underline onClick={() => setPassUpdate(true)}>Забыли пароль?</Text>
            </div>
            <Form.Item
                hidden={!isRegistration}
                label="Повторите пароль"
                name="password2"
                dependencies={['password']}
                rules={[
                    {
                        required: isRegistration,
                        message: 'Пароли должны совпадать',
                    },
                    ({getFieldValue}) => ({
                        validator(_, value) {
                            if (!value || getFieldValue('password') === value) {
                                return Promise.resolve();
                            }
                            return Promise.reject(new Error('Пароли должны совпадать'));
                        },
                    }),
                ]}
            >
                <Input.Password/>
            </Form.Item>
            <Form.Item
                hidden={!isRegistration}
                label="Имя"
                name="name"
                rules={[
                    {

                        message: 'не корректно',
                        required: false,
                    },
                ]}
            >
                <Input/>
            </Form.Item>
            <Form.Item
                hidden={!isRegistration}
                label="Фамилия"
                name="surname"
                rules={[
                    {
                        message: 'введите фамилию',
                        required: false,
                    },
                ]}
            >
                <Input/>
            </Form.Item>
            <Form.Item
                hidden={!isRegistration}
                label="Дата рождения"
                name="birthday"
                rules={[
                    {

                        message: 'введите дату',
                        type: 'date',
                        required: false,
                    },
                ]}
            >
                <DatePicker sty placeholder=""/>
            </Form.Item>

            <ReCAPTCHA
                sitekey="6LcxwlwpAAAAAPYnpB_TJ31xD4mva_PMWcpWnVMp"
                onChange={recap}
            />,
            <Form.Item style={{textAlign: 'center'}}>
                <Button type="primary" htmlType="submit" style={{width: 200, height: 40, fontSize: 18,  backgroundColor:'#722ed1'}}
                        disabled = {!buttonActiv}
                        onClick={() => {
                            setMessage('')
                            form
                                .validateFields()
                                .then((values) => {
                                    onCreate(values, user, isRegistration, 'USER' )
                                        .then(result => {
                                            if (result === true) {
                                                console.log(result)
                                                setIsRegistration(false);
                                                form.resetFields();
                                                onCancel();
                                                setMessage('')
                                            } else {
                                                isRegistration?
                                                    setMessage("Данный email уже зарегестрирован")
                                                    :
                                                    setMessage("Логин и пароль не совпадают")
                                            }
                                        });

                                })
                                .catch((info) => {
                                    console.log('Failed:', info);
                                });
                        }}
                >
                    {isRegistration ?
                        'Зарегестрироваться'
                        :
                        'Войти'
                    }
                </Button>
            </Form.Item>


            <div style={{textAlign: 'center'}}>
                <Link onClick={() => {
                    isRegistration ? setIsRegistration(false)  : setIsRegistration(true);  setMessage('')
                }} underline>
                    {isRegistration ?
                        'Войти'
                        :
                        'Зарегестрироваться'
                    }
                </Link>
            </div>
        </Form>
    );
};

export default  observer(RegLogForm);
